wow = new WOW({
    animateClass: "animated",
    offset: 100,
    callback: function (t) {
        console.log("WOW: animating <" + t.tagName.toLowerCase() + ">")
    }
}),
    wow.init();
var a = 0;
$(window).scroll(function () {
    var t = $("#counter")
        .offset()
        .top - window.innerHeight;
    0 == a && $(window).scrollTop() > t && ($(".b-counter-value").each(function () {
        var t = $(this),
            o = t.attr("data-count");
        $({
            countNum: t.text()
        }).animate({
            countNum: o
        }, {
                duration: 2e3,
                easing: "swing",
                step: function () {
                    t.text(Math.floor(this.countNum).toString().replace(/(\d)(?=(\d\d\d)+(?!\d))/g, "$1,"))
                },
                complete: function () {
                    t.text(this.countNum.toString().replace(/(\d)(?=(\d\d\d)+(?!\d))/g, "$1,"))
                }
            })
    }), a = 1)
})
$(document).ready(function () {

    $(window).scroll(function () {
        var t = $(window).scrollTop(),
            o = 0;
        o = t < 100
            ? t / 100
            : 1,
            $("#b-page-header").css("background-color", "rgba(75, 193, 210, " + o + ")")
    })
});